script.content:
# @METADATASTART #@DetailDescriptionStart ############################################################################# # Extreme Networks(R) CLI Scripting Library # scripting.Script : EAPS Shared-Port Deletion ############################################################################# # Revision : 1.0 # Last Updated : 26-DEC-2009 ############################################################################# # Author Name : NMS Team ############################################################################# # Purpose : ############################################################################# # 1) Delete EAPS Shared-Port ############################################################################# #@DetailDescriptionEnd
# @ScriptDescription "Deletes EAPS Shared-Port." # @SectionStart (description = "EAPS Shared-Port Delete Parameters")
# @VariableFieldLabel (description = "Abort on error?", # type = String, # scope = global, # required = yes, # validValues = [yes,no]) set var abortOnCliError yes
# @VariableFieldLabel (description = "List of comma separated EAPS Shared-Ports", # type = String, # scope = global, # required = yes, # readonly = no) set var eapsSharedPortSeq ""
# # @SectionEnd # @MetaDataEnd ################################################################################ ################################################################################ # CONFIGURATION DETAIL ################################################################################ ################################################################################
############################################################################ # TCL Procedure for CLI scripting.Script Execution Mode setting ############################################################################ proc setCliErrorHandling {inAbortStatus} {
global abort_on_error
if {![string compare $inAbortStatus "yes"]} { configure cli mode scripting abort-on-error } else { configure cli mode scripting ignore-error } }
##################################################################### # TCL Procedure for EAPS Domain Parameter Validation ##################################################################### proc validateEapsSharedPortParams {inSharedPortSeq} {
global abort_on_error global vendor
IF (![string equal $vendor "Extreme"]) THEN error "This script is only supported for Extreme switches." ENDIF
IF (![expr [string length $inSharedPortSeq] > 0]) THEN error "FAILED: EAPS Shared-Port List is empty." ENDIF }
################################################################## # TCL Procedure for EAPS Shared-Port Deletion ################################################################## proc deleteEapsSharedPortSeq {inSharedPortSeq} {
global abort_on_error
if {[catch { delete eaps shared-port $inSharedPortSeq } errMsgText ]} { error "FAILED: Cannot delete EAPS Shared-Ports $inSharedPortSeq $errMsgText" } }
################################################################## # TCL Procedure for EAPS Domain and Control VLAN deletion ################################################################## proc handleEapsSharedPortDelete {inSharedPortSeq} {
global abort_on_error global isExos
if {[string match $isExos true]} { create log entry "Starting EAPS Shared-Port Deletion on Switch" }
validateEapsSharedPortParams $inSharedPortSeq
deleteEapsSharedPortSeq $inSharedPortSeq
if {[string match $isExos true]} { create log entry "Finished EAPS Shared-Port Deletion on Switch" } }
############################################################################ # CLI Configuration Starting on the Switch ############################################################################ setCliErrorHandling $abortOnCliError
handleEapsSharedPortDelete $eapsSharedPortSeq
################################################################################ # Sync the device so that deleted EAPS Shared-Port is not re-discovered ################################################################################ PerformSync -device $deviceIP -scope SharedPort
