script.content:
#@MetaDataStart #@DetailDescriptionStart ############################################################################# # Extreme Networks(R) CLI Scripting Library # scripting.Script : Deletes a 802.1Q VLAN Service for Customer ############################################################################# # Revision : 1.0 # Last Updated : 26-DEC-2009 ############################################################################# # Author Name : NMS Team ############################################################################# # Purpose: This script deletes a 802.1Q VLAN based Service at a List of # UNI Port(s). ############################################################################# #@DetailDescriptionEnd
# @ScriptDescription "scripting.Script to delete a List of UNI Port(s) from an existing VLAN" # @SectionStart (description = "VLAN")
# @VariableFieldLabel ( description = "Abort on error?", # type = String, # scope = global, # required = yes, # validValues = [yes,no]) set var abortOnCliError yes
# @VariableFieldLabel (description = "Switch-Platform", # type = String, # scope = device, # required = yes, # validValues = [MODEL_BD88KSSSF,MODEL_BD10K,MODEL_BD12K,MODEL_BD12804R,MODEL_BD20800]) set var ethServPlatformModel ""
# @VariableFieldLabel ( description = "VLAN Name", # type = VLAN_NAME, # scope = global, # required = yes, # readonly = no # ) set var ethServVLanName ""
# @VariableFieldLabel (description = "List of comma separated UNI Ports", # type = String, # scope = device, # required = yes # ) set var ethServUniPortSeq ""
# @VariableFieldLabel (description = "Comma Listed UNI_Port+BWP_Name", # type = String, # scope = device, # required = no) set var ethServUniPortBWPNameSeq ""
# @VariableFieldLabel (description = "Comma Listed BWP_Name:COS_Mode+Meter_Name+ACLName+QPNum", # type = String, # scope = device, # required = no) set var ethServOneRateBWParamsTupleSeq ""
# @VariableFieldLabel (description = "Comma Listed BWP_Name:COS_Mode+Meter_Name+ACLName+QPNum", # type = String, # scope = device, # required = no) set var ethServTwoRateBWParamsTupleSeq ""
# @SectionEnd # @MetaDataEnd ################################################################################
########################################################################### # Procedure to Validate a single 2-Tuple of the form 'UNI_Port+BWP_Name' ########################################################################### proc validateUniPortBWPTuple {inUniPortBWPName outUniPortNum outBWPName} { global abort_on_error
upvar $outUniPortNum tmpUniPortNum upvar $outBWPName tmpBWPName
set tmpUniBWPList [split $inUniPortBWPName '+']
if {[llength $tmpUniBWPList] != 2} { error "Invalid UNI-Port, BWP-Name Tuple." } set tmpUniPortNum [lindex $tmpUniBWPList 0] set tmpBWPName [lindex $tmpUniBWPList 1] }
########################################################################### # Procedure to Validate a single 10-Tuple of the form # Tuple => 'BWP_Name:COS_Mode+Meter_Name+CIR_V+CIR_U+CBS_V+CBS_U+OOP_ACT+SCH_T+ACLName+QPNum' ########################################################################### proc validateOneRateBWParamsTuple {inBWPNameParamsTuple outBWPName outCOSMode outMeterName outACLName outQPNum} { global abort_on_error
upvar $outBWPName tmpBWPName upvar $outCOSMode tmpCOSMode upvar $outMeterName tmpMeterName upvar $outACLName tmpACLName upvar $outQPNum tmpQPNum
set tmpBWPNameParamsList [split $inBWPNameParamsTuple ':']
if {[llength $tmpBWPNameParamsList] != 2} { error "Invalid BWP-Name, BWP-Params Tuple." } set tmpBWPName [lindex $tmpBWPNameParamsList 0] set tmpBWPAttrPSV [lindex $tmpBWPNameParamsList 1]
set tmpBWPAttrList [split $tmpBWPAttrPSV '+']
if {[llength $tmpBWPAttrList] != 4} { error "Invalid BWP-Attrs Tuple." }
set tmpCOSMode [lindex $tmpBWPAttrList 0] set tmpMeterName [lindex $tmpBWPAttrList 1] set tmpACLName [lindex $tmpBWPAttrList 2] set tmpQPNum [lindex $tmpBWPAttrList 3] }
########################################################################### # Procedure to Validate Comma Listed N-Tuple(s) of the form # Tuple => 'BWP_Nam:COS_Mode+Meter_Name+CIR_V+CIR_U+CBS_V+CBS_U+EIR_V+EIR_U+EBS_V+EBS_U+SCH_T+ACLName+QPNum' ########################################################################### proc validateTwoRateBWParamsTuple {inBWPNameParamsTuple outBWPName outCOSMode outMeterName outACLName outQPNum} { global abort_on_error
upvar $outBWPName tmpBWPName upvar $outCOSMode tmpCOSMode upvar $outMeterName tmpMeterName upvar $outACLName tmpACLName upvar $outQPNum tmpQPNum
set tmpBWPNameParamsList [split $inBWPNameParamsTuple ':']
if {[llength $tmpBWPNameParamsList] != 2} { error "Invalid BWP-Name, BWP-Params Tuple." } set tmpBWPName [lindex $tmpBWPNameParamsList 0] set tmpBWPAttrPSV [lindex $tmpBWPNameParamsList 1]
set tmpBWPAttrList [split $tmpBWPAttrPSV '+']
if {[llength $tmpBWPAttrList] != 4} { error "Invalid BWP-Attrs Tuple." }
set tmpCOSMode [lindex $tmpBWPAttrList 0] set tmpMeterName [lindex $tmpBWPAttrList 1] set tmpACLName [lindex $tmpBWPAttrList 2] set tmpQPNum [lindex $tmpBWPAttrList 3] }
############################################################################# # Procedure to Validate Comma Listed 2-Tuples of the form 'UNI_Port+BWP_Name' ############################################################################# proc validateUniPortToBWPNameTupleSeq {inUniPortToBWPNameSeq outUniPortList outBWPNameList} { global abort_on_error
if {![string length $inUniPortToBWPNameSeq]} { return }
set tmpUniPortBWPNameList [split $inUniPortToBWPNameSeq ,]
if {![llength $tmpUniPortBWPNameList]} { error "UNI-Port, BWP-Name Tuple List cannot be empty." }
upvar $outUniPortList tmpUniPortList upvar $outBWPNameList tmpBWPNameList
foreach currTuple $tmpUniPortBWPNameList {
set outUniPortNum "" set outBWPName ""
validateUniPortBWPTuple $currTuple \ outUniPortNum \ outBWPName
lappend tmpUniPortList $outUniPortNum lappend tmpBWPNameList $outBWPName } }
########################################################################### # Procedure to Validate Comma Listed N-Tuple(s) of the form # Tuple => 'BWP_Name:CIR_V+CIR_U+CBS_V+CBS_U+SCH_T' ########################################################################### proc validateOneRateBWParamsTupleSeq {inBWPNameToParamsSeq outBWPNameToParamsMap} { global abort_on_error
if {![string length $inBWPNameToParamsSeq]} { return }
set tmpBWPNameParamsList [split $inBWPNameToParamsSeq ,]
if {![llength $tmpBWPNameParamsList]} { error "BWP-Name, BWP-Params Tuple List cannot be empty." }
upvar $outBWPNameToParamsMap tmpBWPNameToParamsMap
foreach currTuple $tmpBWPNameParamsList {
set outBWPName "" set outCOSMode "" set outMeterName "" set outACLName "" set outQPNum ""
validateOneRateBWParamsTuple $currTuple \ outBWPName \ outCOSMode \ outMeterName \ outACLName \ outQPNum
set tmpBWPNameToParamsMap($outBWPName.COSMode) $outCOSMode set tmpBWPNameToParamsMap($outBWPName.MeterName) $outMeterName set tmpBWPNameToParamsMap($outBWPName.ACLName) $outACLName set tmpBWPNameToParamsMap($outBWPName.QPNum) $outQPNum } }
########################################################################### # Procedure to Validate Comma Listed N-Tuple(s) of the form # Tuple => 'BWP_Nam:COS_Mode+Meter_Name+CIR_V+CIR_U+CBS_V+CBS_U+EIR_V+EIR_U+EBS_V+EBS_U+SCH_T+ACLName+QPNum' ########################################################################### proc validateTwoRateBWParamsTupleSeq {inBWPNameToParamsSeq outBWPNameToParamsMap} { global abort_on_error
if {![string length $inBWPNameToParamsSeq]} { return }
set tmpBWPNameParamsList [split $inBWPNameToParamsSeq ,]
if {![llength $tmpBWPNameParamsList]} { error "BWP-Name, BWP-Params Tuple List cannot be empty." }
upvar $outBWPNameToParamsMap tmpBWPNameToParamsMap
foreach currTuple $tmpBWPNameParamsList {
set outBWPName "" set outCOSMode "" set outMeterName "" set outACLName "" set outQPNum ""
validateTwoRateBWParamsTuple $currTuple \ outBWPName \ outCOSMode \ outMeterName \ outACLName \ outQPNum
set tmpBWPNameToParamsMap($outBWPName.COSMode) $outCOSMode set tmpBWPNameToParamsMap($outBWPName.MeterName) $outMeterName set tmpBWPNameToParamsMap($outBWPName.ACLName) $outACLName set tmpBWPNameToParamsMap($outBWPName.QPNum) $outQPNum } }
########################################################################## # Procedure to Cleanup BW-Profile Objects (Meter,ACL) ########################################################################## proc deleteOneBWPObj {inMeterName inACLName} {
if {[catch { delete access-list $inACLName } errMsgText]} { puts "FAILED: Could not delete access-list $inACLName, $errMsgText" } if {[catch { delete meter $inMeterName } errMsgText]} { puts "FAILED: Could not delete meter $inMeterName, $errMsgText" }
}
########################################################################## # Procedure to Cleanup BW-Profile Attachment from one UNI Port ########################################################################## proc deleteOneUniBWPAttachment {inUniPortNum inACLName} { global abort_on_error if {[catch { configure access-list delete $inACLName ports $inUniPortNum ingress } errMsgText]} { puts "FAILED: Could not delete $inACLName from UNI port $inUniPortNum, $errMsgText" } }
############################################################################ # Cleanup QoS Objects (Meter, ACL etc.) for a List of BW-Profile(s) ############################################################################ proc deleteAllBWPObjs {inBWPNameList inBWPNameToParamsMap} {
upvar $inBWPNameToParamsMap tmpBWPNameToParamsMap
foreach currBWPName $inBWPNameList {
set currMeterName $tmpBWPNameToParamsMap($currBWPName.MeterName) set currACLName $tmpBWPNameToParamsMap($currBWPName.ACLName)
deleteOneBWPObj $currMeterName $currACLName } }
########################################################################## # Procedure to Cleanup BW-Profile Attachment from all UNI Port(s) ########################################################################## proc deleteAllUniBWPAttachment {inUniPortList inBWPNameList inBWPNameToParamsMap} {
upvar $inBWPNameToParamsMap tmpBWPNameToParamsMap
foreach currUniPort $inUniPortList \ currBWPName $inBWPNameList {
set currACLName $tmpBWPNameToParamsMap($currBWPName.ACLName)
deleteOneUniBWPAttachment $currUniPort $currACLName } }
############################################################################ # Bandwidth Profile assignment to Service End-Points on BD88KSSSF ############################################################################ proc unassignAllUniBWPOnBD88KSSSF {inUniPortList inBWPNameList inBWPNameToParamsMap} {
if {![llength $inBWPNameList]} { return }
upvar $inBWPNameToParamsMap tmpBWPNameToParamsMap
deleteAllUniBWPAttachment $inUniPortList \ $inBWPNameList \ tmpBWPNameToParamsMap
deleteAllBWPObjs $inBWPNameList tmpBWPNameToParamsMap }
############################################################################ # Bandwidth Profile assignment to Service End-Points on BD20800 ############################################################################ proc unassignAllUniBWPOnBD20800 {inUniPortList inBWPNameList inBWPNameToParamsMap} {
if {![llength $inBWPNameList]} { return }
upvar $inBWPNameToParamsMap tmpBWPNameToParamsMap
deleteAllUniBWPAttachment $inUniPortList \ $inBWPNameList \ tmpBWPNameToParamsMap
deleteAllBWPObjs $inBWPNameList tmpBWPNameToParamsMap }
############################################################################ # TCL Procedure for CLI scripting.Script Execution Mode setting ############################################################################ proc setCliErrorHandling {inAbortStatus} {
global abort_on_error
if {![string compare $inAbortStatus "yes"]} { configure cli mode scripting abort-on-error } else { configure cli mode scripting ignore-error } }
############################################################################ # Define TCL Procedures for Service Parameter Validation ############################################################################ proc validateVLanServiceParams {inVLanName inUniPortSeq} {
global abort_on_error global vendor
IF (![string equal $vendor "Extreme"]) THEN error "This script is only supported for Extreme switches." ENDIF
IF (![expr [string length $inVLanName] > 0]) THEN error "VLAN name can not be empty." ENDIF
IF (![expr [string length $inUniPortSeq] > 0]) THEN error "VLAN service must have at least one UNI port." ENDIF }
############################################################################ # TCL Procedure for VLAN Addition to UNI Ports for Service Creation ############################################################################ proc deleteVLanServiceOnUni {inVLanName inUniPortSeq} {
global abort_on_error
if {[catch { configure vlan $inVLanName delete port $inUniPortSeq } result]} { error "FAILED: Cannot delete Ports $inUniPortSeq from VLAN, $result" }
}
############################################################################ # TCL Procedure for PB-Service Creation BD88K, SummitStack and Summit Family ############################################################################ proc runVLanServiceDeleteOnBD88KSSSF {inVLanName inUniPortSeq inUniPortList inBWPNameList inBWPNameToParamsMap} {
upvar $inBWPNameToParamsMap tmpBWPNameToParamsMap
unassignAllUniBWPOnBD88KSSSF $inUniPortList \ $inBWPNameList \ tmpBWPNameToParamsMap
deleteVLanServiceOnUni $inVLanName \ $inUniPortSeq }
############################################################################ # TCL Procedure for PB-Service Creation on BD10K Platform ############################################################################ proc runVLanServiceDeleteOnBD10K {inVLanName inUniPortSeq} {
deleteVLanServiceOnUni $inVLanName \ $inUniPortSeq }
############################################################################ # TCL Procedure for PB-Service Creation on BD12K Platform ############################################################################ proc runVLanServiceDeleteOnBD12K {inVLanName inUniPortSeq} {
deleteVLanServiceOnUni $inVLanName \ $inUniPortSeq }
############################################################################ # TCL Procedure for PB-Service Creation on BD12804R Platform ############################################################################ proc runVLanServiceDeleteOnBD12804R {inVLanName inUniPortSeq} {
deleteVLanServiceOnUni $inVLanName \ $inUniPortSeq
###################################################### ### HQoS based B/W-Profile Deletion for Next Release ###################################################### ## unassignAllUniBWPOnBD12804R $inVLanId \ ## $inUniPortList \ ## $inBWPNameList \ ## tmpBWPNameToParamsMap ###################################################### }
############################################################################ # TCL Procedure for PB-Service Creation on BD20800 Platform ############################################################################ proc runVLanServiceDeleteOnBD20800 {inVLanName inUniPortSeq inUniPortList inBWPNameList inBWPNameToParamsMap} {
upvar $inBWPNameToParamsMap tmpBWPNameToParamsMap
unassignAllUniBWPOnBD20800 $inUniPortList \ $inBWPNameList \ tmpBWPNameToParamsMap
deleteVLanServiceOnUni $inVLanName \ $inUniPortSeq }
############################################################################ # TCL Procedure for PB based Service Creation ############################################################################ proc handleVLanServiceDelete {inVLanName inPlatformModel inUniPortSeq inUniPortToBWPNameSeq inOneRateBWParamsTupleSeq inTwoRateBWParamsTupleSeq} { global abort_on_error global isExos
if {[string match $isExos true]} { create log entry "START-Delete 802.1Q VLAN Service" }
validateVLanServiceParams $inVLanName \ $inUniPortSeq
set outBWPUniPortList [list] set outBWPNameList [list] set outBWPNameToParamsMap(VLanName) $inVLanName
validateUniPortToBWPNameTupleSeq $inUniPortToBWPNameSeq \ outBWPUniPortList \ outBWPNameList switch $inPlatformModel { MODEL_BD88KSSSF { validateOneRateBWParamsTupleSeq $inOneRateBWParamsTupleSeq \ outBWPNameToParamsMap
runVLanServiceDeleteOnBD88KSSSF $inVLanName \ $inUniPortSeq \ $outBWPUniPortList \ $outBWPNameList \ outBWPNameToParamsMap } MODEL_BD10K { runVLanServiceDeleteOnBD10K $inVLanName \ $inUniPortSeq } MODEL_BD12K { runVLanServiceDeleteOnBD12K $inVLanName \ $inUniPortSeq } MODEL_BD12804R { runVLanServiceDeleteOnBD12804R $inVLanName \ $inUniPortSeq } MODEL_BD20800 { if (![expr [string length $inOneRateBWParamsTupleSeq] > 0]) { validateTwoRateBWParamsTupleSeq $inTwoRateBWParamsTupleSeq \ outBWPNameToParamsMap } else { validateOneRateBWParamsTupleSeq $inOneRateBWParamsTupleSeq \ outBWPNameToParamsMap }
runVLanServiceDeleteOnBD20800 $inVLanName \ $inUniPortSeq \ $outBWPUniPortList \ $outBWPNameList \ outBWPNameToParamsMap } default { error "FAILED: Invalid Switch Platform $inPlatformModel" } }
if {[string match $isExos true]} { create log entry "FINISH-Delete 802.1Q VLAN Service" } }
############################################################################ # CLI Configuration Starting ############################################################################ setCliErrorHandling $abortOnCliError
handleVLanServiceDelete $ethServVLanName \ $ethServPlatformModel \ $ethServUniPortSeq \ $ethServUniPortBWPNameSeq \ $ethServOneRateBWParamsTupleSeq \ $ethServTwoRateBWParamsTupleSeq
PerformSync -device $deviceIP -scope VLAN -vlan $ethServVLanName
